import './App.css';
import { useState } from 'react';

const App = () => {
  return (
    <div className="main">
      <Count/>
      <List/>
      <NewTodos/>
    </div>
  );
}


const Count = () => {
  return <div className="count">Total Todos: </div>
}

const List = () => {
  return(
      <ul className="list">
        <li>item1</li>
        <li>item2</li>
        <li>item3</li>
      </ul>
  )
}

const NewTodos = () => {

  const [input, setInput] = useState('');

  const setInputHandler = event => {
    setInput(event.target.value);
  }

  return(
    <form>
      <input type="text" id="todo" 
        onChange={setInputHandler}/>
      <button type="submit" id="btn" onClick={()=>{ console.log(input) }}>Add Todo</button>
    </form>
  )
}


export default App;
